import React from "react";
import style from "./FoodForm.module.css";
import InputFood from "../../UI/InputFood";

const FoodForm = ({ id }) => {
  return (
    <form className={style.form}>
      <InputFood
        label="Amount"
        input={{
          id: "amount_" + id,
          type: "number",
          min: 1,
          max: 10,
          defaultValue: 1,
        }}
      />
      <button>Add</button>
    </form>
  );
};

export default FoodForm;
